const int time_delay = 1;

int getOldestIssuedCommand(){
	int c = 0;
	int i;
	int oldest = 0;
	for(i = 1; i < trace - 1; i++){
		if (CommandTrace[oldest].timestamp > CommandTrace[i].timestamp){
			oldest = i;
		}
	}
	return oldest;
}

void sortTrace(){
	int i,j;
	command larger;
        for (i = 0; i < trace; ++i) 
        {
            for (j = i + 1; j < trace; ++j)
            {
                if (CommandTrace[i].timestamp > CommandTrace[j].timestamp) 
                {
                    larger = CommandTrace[i];
                    CommandTrace[i] = CommandTrace[j];
                    CommandTrace[j] = larger;
                }
            }
        }
	return;
}

void ExecuteCommand(){
    cd.executed = true;
    CommandTrace[command_counter] = cd;
    command_counter++;
    return;
}

int Wrap(int current){
    if((current + 1) > trace -1){
        return 0;
    }
    return current + 1;
}

void SetPropertyIndices(int oldest){
    first = Wrap(oldest); // here oldest actually becomes newest
    second = Wrap(first);
    third = Wrap(second);
    fourth = Wrap(third);
    fifth = Wrap(fourth);
    return;
}

bool IsMaliciousTrace(){
    bool is_short;
    int i = 0;
    if((CommandTrace[first].timestamp - CommandTrace[second].timestamp) <= time_delay && (CommandTrace[second].timestamp - CommandTrace[third].timestamp) <= time_delay 
      && CommandTrace[third].timestamp - CommandTrace[fourth].timestamp <= time_delay && CommandTrace[fourth].timestamp - CommandTrace[fifth].timestamp <= time_delay){
      is_short = true;  
    } 
    cmd_short = is_short;
    if(CommandTrace[first].executed && is_short){
        if(((CommandTrace[first].Type == speedc) && (CommandTrace[first].Value == min_spd)) && ((CommandTrace[second].Type == speedc) && (CommandTrace[second].Value == max_spd)) 
            && ((CommandTrace[third].Type == speedc) && (CommandTrace[third].Value == min_spd)) && ((CommandTrace[fourth].Type == speedc) && (CommandTrace[fourth].Value == max_spd)) 
            && ((CommandTrace[fifth].Type == speedc) && (CommandTrace[fifth].Value == min_spd))){ // sequence 1, min to max power continuously
            return true;
        }
        else if(((CommandTrace[first].Type == speedc) && (CommandTrace[first].Value == max_spd)) && ((CommandTrace[second].Type == speedc) && (CommandTrace[second].Value == min_spd)) 
            && ((CommandTrace[third].Type == speedc) && (CommandTrace[third].Value == max_spd)) && ((CommandTrace[fourth].Type == speedc) && (CommandTrace[fourth].Value == min_spd)) 
            && ((CommandTrace[fifth].Type == speedc) && (CommandTrace[fifth].Value == max_spd))){ // sequence 1, the other way around
            return true;  
        }
        else if(((CommandTrace[first].Type == dirc) && (CommandTrace[first].Value == forward)) && ((CommandTrace[second].Type == dirc) && (CommandTrace[second].Value == reverse))
            && ((CommandTrace[third].Type == dirc) && (CommandTrace[third].Value == forward)) && ((CommandTrace[fourth].Type == dirc) && (CommandTrace[fourth].Value == reverse))
            && ((CommandTrace[fifth].Type == dirc) && (CommandTrace[fifth].Value == forward))){ // sequence 2, forward -> reverse continuously
            return true;
        }
        else if(((CommandTrace[first].Type == dirc) && (CommandTrace[first].Value == reverse)) && ((CommandTrace[second].Type == dirc) && (CommandTrace[second].Value == forward))
            && ((CommandTrace[third].Type == dirc) && (CommandTrace[third].Value == reverse)) && ((CommandTrace[fourth].Type == dirc) && (CommandTrace[fourth].Value == forward))
            && ((CommandTrace[fifth].Type == dirc) && (CommandTrace[fifth].Value == reverse))){ // sequence 2, reverse -> forward continuously
            return true;
        }
    }
	return false;
}

void HandleCommand(){
    duration = 0;
	if(is_init == true){
		if (command_counter == trace - 1){
			cd.executed = true;
			CommandTrace[command_counter] = cd;
			is_init = false;
		}else{
			cd.executed = true;
			CommandTrace[command_counter] = cd;
            command_counter++;
		}
	}else{
		int oldest = getOldestIssuedCommand();
		CommandTrace[oldest] = cd;
        SetPropertyIndices(oldest);
        malicious_trace = IsMaliciousTrace(); //raise malicious trace
	}
	return;
}

